create table slowko (
	eng NCHAR(20) NOT NULL PRIMARY KEY,	pl NVARCHAR(20) NOT NULL);create table rola (	id INT NOT NULL PRIMARY KEY IDENTITY(1,1),	nazwa NVARCHAR(15) NOT NULL UNIQUE);create table ustawieniaZagadki (	id INT NOT NULL PRIMARY KEY IDENTITY(1,1),	opis NVARCHAR(50) NOT NULL UNIQUE);create table uzytkownik (	nazwa NCHAR(20) NOT NULL PRIMARY KEY,	haslo NCHAR(20) NOT NULL,	rola INT NOT NULL,	ustawienia INT NOT NULL,	FOREIGN KEY (rola)		REFERENCES rola(id),	FOREIGN KEY (ustawienia)		REFERENCES ustawieniaZagadki(id));create table statystykaUzytkownika (	nazwa NCHAR(20) NOT NULL PRIMARY KEY,	dobreOdpowiedzi INT NOT NULL DEFAULT 0,	zleOdpowiedzi INT NOT NULL DEFAULT 0,	FOREIGN KEY (nazwa)		REFERENCES uzytkownik(nazwa));